    # Converts each token into lowercase and remove stopwords
    tokens2=[]
    for i in range(len(tokens)):
        tokens[i] = tokens[i].lower()
        if(tokens[i] not in stop_words):
            tokens2.append(tokens[i])
    
    comment_words += " ".join(tokens2)+" "

